class Game{constructor(t,e){this.width=t,this.height=e}start(){this.paddleL=new Paddle(this,!0),this.paddleR=new Paddle(this,!1),this.ball=new Ball(this),this.gameObjects=[this.ball,this.paddleL,this.paddleR],new InputHandler(this.paddleL,this.paddleR)}update(t){this.gameObjects.forEach(e=>e.update(t))}}