class InputHandler{constructor(e,o){document.addEventListener("keydown",t=>{switch(t.keyCode){case 38:e.moveUp(),o.moveUp();break;case 40:e.moveDown(),o.moveDown()}}),document.addEventListener("keyup",t=>{switch(t.keyCode){case 38:e.direction<0&&e.stop(),o.direction<0&&o.stop();break;case 40:e.direction>0&&e.stop(),o.direction>0&&o.stop()}})}}